import {
    Box,
    Flex,
    Button,
    useColorModeValue,
    Stack,
    useColorMode,
    Show,
    HStack,
    Text,
    useDisclosure,
    IconButton,
    Hide,
    L
  } from "@chakra-ui/react";
  import {
    MoonIcon,
    SunIcon,
    HamburgerIcon,
    CloseIcon,
    AddIcon,
  } from "@chakra-ui/icons";
//   import Photo from "./Photo";
  import Name from "./Name";
  import { HashLink as ReachLink } from "react-router-hash-link";
  import "./Navbar.css";
//   import Shivam_Resume from "./Shivam_Resume.pdf";
  
  export default function Navbar() {
    const { colorMode, toggleColorMode } = useColorMode();
    const { isOpen, onOpen, onClose } = useDisclosure();
    const navItem = [
      { id: 1, path: "#home", title: "Home", class: "nav-link home" },
      { id: 2, path: "#about", title: "About", class: "nav-link about" },
      { id: 3, path: "#skills", title: "Skills", class: "nav-link skills" },
      { id: 4, path: "#projects", title: "Projects", class: "nav-link projects" },
      { id: 5, path: "#contact", title: "Contact", class: "nav-link contact" },
      {
        id: 6,
        path: "https://drive.google.com/file/d/1JqqV7I-lz5vjhj5Q48pTuRFDD01X_Rg7/view?usp=sharing",
        title: "Resume",
        class: "nav-link resume resume-button-1",
      },
    ];
  
    // const onButtonClick = () => {
    //   window.open(Shivam_Resume);
    // };
  
    return (
      <div id="nav-menu">
        <Box
          bg={useColorModeValue("gray.100", "gray.900")}
          px={9}
          width={["100%"]}
        >
          <Flex h={16} alignItems={"center"} justifyContent={"space-between"}>
            <HStack w="42%">
              <Name />
  
              <Show breakpoint="(min-width: 1000px)">
                {" "}
                {/* <Photo /> */}
              </Show>
            </HStack>
  
            <Flex h={16} alignItems={"center"} justifyContent={"space-between"}>
              {/* <IconButton
                size={"md"}
                icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
                aria-label={"Open Menu"}
                display={{ md: "none" }}
                onClick={isOpen ? onClose : onOpen}
              /> */}
              <HStack spacing={8} alignItems={"center"}>
                <HStack
                  as={"nav"}
                  spacing={4}
                  display={{ base: "none", md: "flex" }}
                  id="myDIV"
                >
                  <Flex gap="2" display={{ base: "none", md: "none", lg: "flex" }}>
          {navItem?.map((el) => {
            return (
              <Link
                key={el.id}
                as={ReachLink}
                id={el.title == "Resume" ? "resume-link-1" : ""}
                {...(active == el.path
                  ? { ...linkTheme, bg: "#FF4D05" }
                  : { ...linkTheme })}
                _hover={{ bg: "#FF4D05" }}
                to={el.path}
                className={el.class}
                target={el.title == "Resume" ? "_blank" : "_self"}
                // {...(el.title == "Resume" ? "download" : "")}
              >
                {el.title}
              </Link>
            );
          })}
        </Flex>




               {/* <NavLink to="#home" > <Button className="btnRes">
                      <b>Home</b>
                  </Button></NavLink>
  <NavLink to="#about"><Button className="btnRes">
                      <b>About</b>
                  </Button></NavLink>
                   

                   <NavLink to="#skills" >
                  <Button className="btnRes">
                      <b>Skills</b>
                  </Button>
                  </NavLink>
  
                 <NavLink to="/project"> <Button className="btnRes">
                      <b>Projects</b>
                  </Button></NavLink>
  
                 <NavLink to="contact"> <Button className="btnRes">
                      <b>Contact</b>
                  </Button></NavLink> */}
                </HStack>
              </HStack>
            </Flex>
  
            {/* {isOpen ? (
              <Box pb={4} display={{ md: "none" }}>
                <Stack as={"nav"} spacing={4}>
                  <NavLink to="/" > <Button className="btnRes">
                      <b>Home</b>
                  </Button></NavLink>
  <NavLink to="/about"><Button className="btnRes">
                      <b>About</b>
                  </Button></NavLink>
                   

                   <NavLink to="/skill" >
                  <Button className="btnRes">
                      <b>Skills</b>
                  </Button>
                  </NavLink>
  
                 <NavLink to="/project"> <Button className="btnRes">
                      <b>Projects</b>
                  </Button></NavLink>
  
                 <NavLink to="contact"> <Button className="btnRes">
                      <b>Contact</b>
                  </Button></NavLink>
                </Stack>
              </Box>
            ) : null} */}
  
            <Flex alignItems={"center"}>
              <Stack direction={"row"} spacing={7}>
                <Button onClick={toggleColorMode}>
                  {colorMode === "light" ? <MoonIcon /> : <SunIcon />}
                </Button>
  
                <Button
                  backgroundColor="#a891b7"
                  _hover={{ bg: "#a891b7", color: "black" }}
                  color="white"
                  variant="solid"
                //   onClick={onButtonClick}
                  size={["sm", "md"]}
                  download="Shivam_Resume"
                  id="resumeBtn"
                >
                  <a
                    // href={Shivam_Resume}
                    // target="_blank"
                    // download="Shivam_Resume"
                  >
                    RESUME
                  </a>
                  {/* <Link
                      id="navRes"
                      href={Shivam_Resume}
                      target="_blank"
                      style={{ textDecoration: "none", color: "white" }}
                      download="Shivam_Resume"
                    >
                      RESUME
                    </Link> */}
                </Button>
              </Stack>
            </Flex>
            <IconButton
              size={"md"}
              icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
              aria-label={"Open Menu"}
              display={{ md: "none" }}
              onClick={isOpen ? onClose : onOpen}
            />
            {isOpen ? (
              <Box pb={4} display={{ md: "none" }}>
                <Stack as={"nav"} spacing={4}>
                  <Button  onClick={isOpen ? onClose : onOpen}
                    _hover={{
                      textShadow: "#FC0 1px 0 10px",
                      transform: "scale(1.2)",
                    }}>
                    <a href="#Home">
                      {" "}
                      <b>Home</b>
                    </a>
                  </Button>
                  <Button  onClick={isOpen ? onClose : onOpen}
                    _hover={{
                      textShadow: "#FC0 1px 0 10px",
                      transform: "scale(1.2)",
                    }}>
                    <a href="#Home">
                      {" "}
                      <b>Home</b>
                    </a>
                  </Button>
  
                  <Button  onClick={isOpen ? onClose : onOpen}
                    _hover={{
                      textShadow: "#FC0 1px 0 10px",
                      transform: "scale(1.2)",
                    }}>
                    <a href="#Home">
                      {" "}
                      <b>Home</b>
                    </a>
                  </Button>
                  <Button  onClick={isOpen ? onClose : onOpen}
                    _hover={{
                      textShadow: "#FC0 1px 0 10px",
                      transform: "scale(1.2)",
                    }}>
                    <a href="#Home">
                      {" "}
                      <b>Home</b>
                    </a>
                  </Button>
                  <Button
                    onClick={isOpen ? onClose : onOpen}
                    _hover={{
                      textShadow: "#FC0 1px 0 10px",
                      transform: "scale(1.2)",
                    }}
                  >
                 <NavLink to="/"><b>Home</b></NavLink>
                     
                   
                  </Button>
  
                  <Button
                    onClick={isOpen ? onClose : onOpen}
                    _hover={{
                      textShadow: "#FC0 1px 0 10px",
                      transform: "scale(1.2)",
                    }}
                  >
                   
                    <NavLink to="/about"><b>about</b></NavLink>
                  
                  </Button>
  
                  <Button
                    onClick={isOpen ? onClose : onOpen}
                    _hover={{
                      textShadow: "#FC0 1px 0 10px",
                      transform: "scale(1.2)",
                    }}
                  >
                   
                  
                      <NavLink to="/skill"><b>Skills</b></NavLink>
                 
                  </Button>
  
                  <Button
                    onClick={isOpen ? onClose : onOpen}
                    _hover={{
                      textShadow: "#FC0 1px 0 10px",
                      transform: "scale(1.2)",
                    }}
                  >
                   
                    <NavLink to="/project"><b>Projects</b></NavLink>
                   
                  </Button>
  
                  <Button
                    onClick={isOpen ? onClose : onOpen}
                    _hover={{
                      textShadow: "#FC0 1px 0 10px",
                      transform: "scale(1.2)",
                    }}
                  >
                    <NavLink to="/contact"><b>Contact</b></NavLink>
                
                  </Button>
                </Stack>
              </Box>
            ) : null}
          </Flex>
        </Box>
      </div>
    );
  }
