import { Heading, Text } from "@chakra-ui/react";
import React, { Component } from "react";
import Slider from "react-slick";
import {AiFillGithub,AiFillHtml5} from 'react-icons/ai'
import { Icon } from "@chakra-ui/react";
export default class LinksHint extends Component {
  render() {
    var settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0, 
      autoplay: true,
      speed: 4000,
      autoplaySpeed: 1000,
      cssEase: "linear",
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };

    let Data=[
     {icons:AiFillGithub,name:'Github'},
     {icons:AiFillHtml5,name:'Github'},
     {icons:AiFillHtml5,name:'Github'},
     {icons:AiFillHtml5,name:'Github'},
     {icons:AiFillHtml5,name:'Github'},
     {icons:AiFillHtml5,name:'Github'},
  ]
    return (
      <div>
        <Heading>Skills</Heading>
        <Slider {...settings}>
          {Data.map((el,i)=>{
            return <div key={i+1} >
              <div  style={{boxShadow:"0px 2px 10px 1px ",margin:'10px'}} >
             
              <Icon as={el.icons} fontSize='5rem' />
              <Text>{el.name}</Text>
              </div>
              </div>
          })}
        </Slider>
      </div>
    );
  }
}
