import { Box, Heading, Text } from "@chakra-ui/react";
import React, { Component } from "react";
import Slider from "react-slick";
import {AiFillGithub,AiFillHtml5,} from 'react-icons/ai'
import {IoLogoCss3} from 'react-icons/io'
import {SiJavascript} from 'react-icons/si'
import {FaReact} from 'react-icons/fa'
import {TiHtml5} from 'react-icons/ti'
import {SiCanva} from 'react-icons/ti'


import { Icon } from "@chakra-ui/react";
export default class LinksHint extends Component {
  render() {
    var settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0, 
      autoplay: true,
      speed: 4000,
      autoplaySpeed: 1000,
      cssEase: "linear",
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };

    let Data=[
     {icons:AiFillGithub,name:'Github'},
     {icons:IoLogoCss3,name:'CSS'},
     {icons:SiJavascript,name:'JavaScript'},
     {icons:FaReact,name:'React'},

     {icons:TiHtml5,name:'HTML'},
     {icons:SiCanva,name:'Canva'},
  ]
    return (
      <div>
        <Heading>Skills</Heading>
        <Slider {...settings}>
          {Data.map((el,i)=>{
            return <div key={i+1} >
              <div   >
             <Box m='1rem' boxShadow={'lg'}>
             <Icon as={el.icons} fontSize='5rem' />
              <Text>{el.name}</Text>
             </Box>
              </div>
              </div>
          })}
        </Slider>
      </div>
    );
  }
}
