import {
  Box,
  Flex,
  Button,
  useColorModeValue,
  Stack,
  useColorMode,
  Show,
  HStack,
  Text,
  useDisclosure,
  IconButton,
  Hide,
} from "@chakra-ui/react";
import {
  MoonIcon,
  SunIcon,
  HamburgerIcon,
  CloseIcon,
  AddIcon,
} from "@chakra-ui/icons";
// import Photo from "./Photo";
import Name from "./Name";
import "./Navbar.css";
import Shivam_Resume from "./Shivam_Resume.pdf";

export default function Navbar() {
  const { colorMode, toggleColorMode } = useColorMode();
  const { isOpen, onOpen, onClose } = useDisclosure();

  const onButtonClick = () => {
    window.open(Shivam_Resume);
  };

  return (
    <div id="nav-menu" style={{ position: "sticky", top: 0, width: "100%" ,zIndex:1000}}>
      <Box
        bg={useColorModeValue("gray.100", "gray.900")}
        px={9}
        width={["100%"]}
      >
        <Flex h={16} alignItems={"center"} justifyContent={"space-between"}>
          <HStack w="42%">
            <Name />
          </HStack>

          <Flex h={16} alignItems={"center"} justifyContent={"space-between"}>
            <HStack spacing={8} alignItems={"center"}>
              <HStack
                as={"nav"}
                spacing={4}
                display={{ base: "none", md: "flex" }}
                id="myDIV"
              >
                <Button
                  className="nav-link home"
                  _hover={{
                    color: "blue.400",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#home">
                    {" "}
                    <b>Home</b>
                  </a>
                </Button>

                <Button
                  className="nav-link about"
                  _hover={{
                    color: "blue.400",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#about">
                    <b>About</b>
                  </a>
                </Button>

                <Button
                  className="nav-link skills"
                  _hover={{
                    color: "blue.400",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#skills">
                    {" "}
                    <b>Skills</b>
                  </a>
                </Button>

                <Button
                  className="nav-link projects"
                  _hover={{
                    color: "blue.400",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#projects">
                    <b>Projects</b>
                  </a>
                </Button>

                <Button
                  className="nav-link contact"
                  _hover={{
                    color: "blue.400",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#contact">
                    <b>Contact</b>
                  </a>
                </Button>
              </HStack>
            </HStack>
          </Flex>

          <Flex alignItems={"center"}>
            <Stack direction={"row"} spacing={7}>
              <Button onClick={toggleColorMode}>
                {colorMode === "light" ? <MoonIcon /> : <SunIcon />}
              </Button>

              <Button
                id="resume-button-1"
                backgroundColor="#a891b7"
                _hover={{ bg: "#a891b7", color: "black" }}
                color="white"
                variant="solid"
                className="nav-link resume"
                size={["sm", "md"]}
                // onClick={onButtonClick}
  
              >
                <a
                
                  id="resume-link-1"
                  href={Shivam_Resume}
                
                  download="Shivam_Resume"
                >
                  RESUME
                </a>
              </Button>
            </Stack>
          </Flex>
          <IconButton
            size={"md"}
            icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
            aria-label={"Open Menu"}
            display={{ md: "none" }}
            onClick={isOpen ? onClose : onOpen}
          />
          {isOpen ? (
            <Box pb={4} display={{ md: "none" }}>
              <Stack as={"nav"} spacing={4}>
                <Button
                  onClick={isOpen ? onClose : onOpen}
                  _hover={{
                    textShadow: "blue.400 1px 0 10px",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#home">
                    {" "}
                    <b>Home</b>
                  </a>
                </Button>
                <Button
                  onClick={isOpen ? onClose : onOpen}
                  _hover={{
                    textShadow: "blue.400 1px 0 10px",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#home">
                    {" "}
                    <b>Home</b>
                  </a>
                </Button>

                <Button
                  onClick={isOpen ? onClose : onOpen}
                  _hover={{
                    textShadow: "blue.400 1px 0 10px",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#home">
                    {" "}
                    <b>Home</b>
                  </a>
                </Button>
                <Button
                  onClick={isOpen ? onClose : onOpen}
                  _hover={{
                    textShadow: "blue.400 1px 0 10px",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#home">
                    {" "}
                    <b>Home</b>
                  </a>
                </Button>
                <Button
                  onClick={isOpen ? onClose : onOpen}
                  _hover={{
                    textShadow: "blue.400 1px 0 10px",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#home">
                    {" "}
                    <b>Home</b>
                  </a>
                </Button>

                <Button
                  onClick={isOpen ? onClose : onOpen}
                  _hover={{
                    textShadow: "blue.400 1px 0 10px",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#about">
                    <b>About</b>
                  </a>
                </Button>

                <Button
                  onClick={isOpen ? onClose : onOpen}
                  _hover={{
                    textShadow: "blue.400 1px 0 10px",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#skills">
                    {" "}
                    <b>Skills</b>
                  </a>
                </Button>

                <Button
                  onClick={isOpen ? onClose : onOpen}
                  _hover={{
                    textShadow: "blue.400 1px 0 10px",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#projects">
                    <b>Projects</b>
                  </a>
                </Button>

                <Button
                  onClick={isOpen ? onClose : onOpen}
                  _hover={{
                    textShadow: "blue.400 1px 0 10px",
                    transform: "scale(1.2)",
                  }}
                >
                  <a href="#contact">
                    <b>Contact</b>
                  </a>
                </Button>
              </Stack>
            </Box>
          ) : null}
        </Flex>
      </Box>
    </div>
  );
}
